 1→import { describe, it, expect } from "vitest";
 2→import { readFile, writeFile } from "fs/promises";
 3→import { join } from "path";
 4→import matter from "gray-matter";
 5→import { exec } from "child_process";
 6→import { promisify } from "util";
 7→
 8→const execAsync = promisify(exec);
 9→
10→const testFileName = "pr_creation_test.md";
11→const queueDir = join(import.meta.dirname, "..", "request_queue");
12→const testFilePath = join(queueDir, testFileName);
13→
14→describe("PR creation", () => {
15→  it("should process a backlog item and set status to Done", async () => {
16→    // 1. Reset the test file to status "Backlog"
17→    const initialContent = await readFile(testFilePath, "utf-8");
18→    const initialData = matter(initialContent);
19→
20→    await writeFile(
21→      testFilePath,
22→      matter.stringify("", { ...initialData.data, status: "Backlog" })
23→    );
24→
25→    // 2. Run the main loop
26→    const { stdout, stderr } = await execAsync("npx tsx src/index.ts", {
27→      cwd: join(import.meta.dirname, ".."),
28→      maxBuffer: 10 * 1024 * 1024,
29→    });
30→
31→    if (stdout) console.log(stdout);
32→    if (stderr) console.error(stderr);
33→
34→    // 3. Verify the status changed to "Done"
35→    const finalContent = await readFile(testFilePath, "utf-8");
36→    const finalData = matter(finalContent);
37→
38→    expect(finalData.data.status).toBe("Done");
39→  });
40→});
41→

