 83→     *   const x = 10;
 84→     *   const y = 20;
 85→     *   console.log(\`Sum: \${x + y}\`);
 86→     * `);
 87→     * console.log(response.artifacts.stdout);  // Prints: Sum: 30
 88→     *
 89→     * @example
 90→     * // Run Python code with matplotlib
 91→     * const response = await process.codeRun(`
 92→     * import matplotlib.pyplot as plt
 93→     * import numpy as np
 94→     *
 95→     * x = np.linspace(0, 10, 30)
 96→     * y = np.sin(x)
 97→     *
 98→     * plt.figure(figsize=(8, 5))
 99→     * plt.plot(x, y, 'b-', linewidth=2)
100→     * plt.title('Line Chart')
101→     * plt.xlabel('X-axis (seconds)')
102→     * plt.ylabel('Y-axis (amplitude)')
103→     * plt.grid(True)
104→     * plt.show()
105→     * `);
106→     *
107→     * if (response.artifacts?.charts) {
108→     *   const chart = response.artifacts.charts[0];
109→     *
110→     *   console.log(`Type: ${chart.type}`);
111→     *   console.log(`Title: ${chart.title}`);
112→     *   if (chart.type === ChartType.LINE) {
113→     *     const lineChart = chart as LineChart
114→     *     console.log('X Label:', lineChart.x_label)
115→     *     console.log('Y Label:', lineChart.y_label)
116→     *     console.log('X Ticks:', lineChart.x_ticks)
117→     *     console.log('Y Ticks:', lineChart.y_ticks)
118→     *     console.log('X Tick Labels:', lineChart.x_tick_labels)
119→     *     console.log('Y Tick Labels:', lineChart.y_tick_labels)
120→     *     console.log('X Scale:', lineChart.x_scale)
121→     *     console.log('Y Scale:', lineChart.y_scale)
122→     *     console.log('Elements:')
123→     *     console.dir(lineChart.elements, { depth: null })
124→     *   }
125→     * }
126→     */
127→    codeRun(code: string, params?: CodeRunParams, timeout?: number): Promise<ExecuteResponse>;
128→    /**
129→     * Creates a new long-running background session in the Sandbox.
