  1→import { Daytona, Sandbox } from "@daytonaio/sdk";
  2→import matter from "gray-matter";
  3→import { readdir, readFile, writeFile } from "fs/promises";
  4→import { join } from "path";
  5→
  6→interface TaskRequest {
  7→  file: string;
  8→  filePath: string;
  9→  title: string;
 10→  description: string;
 11→  repo: string;
 12→  numberOfSandboxes: number;
 13→  status: string;
 14→}
 15→
 16→export class SandboxQueueProcessor {
 17→  private daytona: Daytona;
 18→  private queueDir: string;
 19→  private config: {
 20→    anthropicApiKey: string;
 21→    githubToken: string;
 22→    claudeModel: string;
 23→  };
 24→
 25→  constructor(daytonaApiKey: string) {
 26→    this.daytona = new Daytona({ apiKey: daytonaApiKey });
 27→    this.queueDir = join(import.meta.dirname, "..", "..", "request_queue");
 28→    this.config = {
 29→      anthropicApiKey: process.env.ANTHROPIC_API_KEY!,
 30→      githubToken: process.env.GITHUB_TOKEN!,
 31→      claudeModel: "claude-sonnet-4-5-20250929",
 32→    };
 33→  }
 34→
 35→  async processQueue(): Promise<void> {
 36→    const tasks = await this.loadTasksFromQueue();
 37→
 38→    for (const task of tasks) {
 39→      console.log(`Processing: ${task.title}`);
 40→      await this.updateTaskStatus(task, "In Progress");
 41→
 42→      const sandboxTasks = Array.from(
 43→        { length: task.numberOfSandboxes },
 44→        (_, i) =>
 45→          this.runInSandbox(
 46→            task.description,
 47→            `${task.title}-${i + 1}`,
 48→            task.repo
 49→          )
 50→      );
 51→      await Promise.all(sandboxTasks);
 52→
 53→      await this.updateTaskStatus(task, "Done");
 54→      console.log(`Completed: ${task.title}`);
 55→    }
 56→  }
 57→
 58→  private async loadTasksFromQueue(): Promise<TaskRequest[]> {
 59→    const files = (await readdir(this.queueDir)).filter((f) =>
 60→      f.endsWith(".md")
 61→    );
 62→    const tasks: TaskRequest[] = [];
 63→
 64→    for (const file of files) {
 65→      const task = await this.parseTaskFile(file, join(this.queueDir, file));
 66→      if (task) {
 67→        tasks.push(task);
 68→      }
 69→    }
 70→
 71→    return tasks;
 72→  }
 73→
 74→  private async parseTaskFile(
 75→    file: string,
 76→    filePath: string
 77→  ): Promise<TaskRequest | null> {
 78→    const raw = await readFile(filePath, "utf-8");
 79→    const { data } = matter(raw);
 80→
 81→    if (data.status !== "Backlog") {
 82→      return null;
 83→    }
 84→
 85→    return {
 86→      file,
 87→      filePath,
 88→      title: data.title,
 89→      description: data.description,
 90→      repo: data.repo,
 91→      numberOfSandboxes: data.number_of_sandboxes,
 92→      status: data.status,
 93→    };
 94→  }
 95→
 96→  private async updateTaskStatus(
 97→    task: TaskRequest,
 98→    status: string
 99→  ): Promise<void> {
100→    const raw = await readFile(task.filePath, "utf-8");
101→    const { data } = matter(raw);
102→    await writeFile(
103→      task.filePath,
104→      matter.stringify("", { ...data, status })
105→    );
106→  }
107→
108→  private async runInSandbox(
109→    prompt: string,
110→    label: string,
111→    repo: string
112→  ): Promise<void> {
113→    const sandbox = await this.daytona.create({ language: "typescript" });
114→    console.log(`[${label}] Sandbox created`);
115→
116→    try {
117→      const repoDir = await this.setupSandboxEnvironment(sandbox, repo, label);
118→      await this.installClaudeCLI(sandbox, label);
119→      await this.installGitHubCLI(sandbox, label);
120→      await this.configureGit(sandbox, label);
121→      await this.executeClaudeCommand(sandbox, prompt, repoDir, label);
122→    } finally {
123→      await sandbox.delete();
124→      console.log(`[${label}] Deleted`);
125→    }
126→  }
127→
128→  private async setupSandboxEnvironment(
129→    sandbox: Sandbox,
130→    repo: string,
131→    label: string
132→  ): Promise<string> {
133→    const repoDir = "/home/daytona/repo";
134→    await sandbox.git.clone(repo, repoDir);
135→    console.log(`[${label}] Repo cloned`);
136→    return repoDir;
137→  }
138→
139→  private async installClaudeCLI(
140→    sandbox: Sandbox,
141→    label: string
142→  ): Promise<void> {
143→    console.log(`[${label}] Installing Claude CLI...`);
144→    const claudeInstall = await sandbox.process.executeCommand(
145→      "mkdir -p ~/.npm-global && npm config set prefix '~/.npm-global' && npm install -g @anthropic-ai/claude-code"
146→    );
147→    console.log(`[${label}] Claude CLI exit code: ${claudeInstall.exitCode}`);
148→
149→    if (claudeInstall.exitCode !== 0) {
150→      console.error(
151→        `[${label}] Failed to install Claude CLI: ${claudeInstall.result}`
152→      );
153→      throw new Error("Failed to install Claude CLI");
154→    }
155→
156→    const claudeVerify = await sandbox.process.executeCommand(
157→      "export PATH=~/.npm-global/bin:$PATH && which claude && claude --version"
158→    );
159→    console.log(`[${label}] Claude CLI location and version: ${claudeVerify.result}`);
160→  }
161→
162→  private async installGitHubCLI(
163→    sandbox: Sandbox,
164→    label: string
165→  ): Promise<void> {
166→    console.log(`[${label}] Installing GitHub CLI from binary...`);
167→    const ghInstall = await sandbox.process.executeCommand(
168→      "GH_VERSION=2.86.0 && mkdir -p ~/bin && curl -fsSL https://github.com/cli/cli/releases/download/v${GH_VERSION}/gh_${GH_VERSION}_linux_amd64.tar.gz -o /tmp/gh.tar.gz && tar -xzf /tmp/gh.tar.gz -C /tmp && cp /tmp/gh_${GH_VERSION}_linux_amd64/bin/gh ~/bin/gh && chmod +x ~/bin/gh && export PATH=~/bin:$PATH"
169→    );
170→    console.log(`[${label}] GitHub CLI install exit code: ${ghInstall.exitCode}`);
171→
172→    if (ghInstall.exitCode !== 0) {
173→      console.error(
174→        `[${label}] Failed to install gh CLI: ${ghInstall.result}`
175→      );
176→      throw new Error("Failed to install gh CLI");
177→    }
178→
179→    const ghVerify = await sandbox.process.executeCommand(
180→      "export PATH=~/bin:$PATH && gh --version"
181→    );
182→    console.log(`[${label}] GitHub CLI version: ${ghVerify.result}`);
183→  }
184→
185→  private async configureGit(sandbox: Sandbox, label: string): Promise<void> {
186→    console.log(`[${label}] Configuring git...`);
187→    await sandbox.process.executeCommand(
188→      'git config --global user.email "claude@anthropic.com"'
189→    );
190→    await sandbox.process.executeCommand(
191→      'git config --global user.name "Claude Agent"'
192→    );
193→    console.log(`[${label}] Git configured`);
194→  }
195→
196→  private async executeClaudeCommand(
197→    sandbox: Sandbox,
198→    prompt: string,
199→    repoDir: string,
200→    label: string
201→  ): Promise<void> {
202→    const fullPrompt = `You are working in a cloned git repo. Your task:
