  private async executeClaudeCommand(
    sandbox: Sandbox,
    prompt: string,
    repoDir: string,
    label: string
  ): Promise<void> {
    const fullPrompt = `You are working in a cloned git repo. Your task:

1. Create a new branch with a descriptive name for this feature
2. Implement the following feature: ${prompt}
3. Commit your changes with a clear commit message
4. Push the branch to origin
5. Create a pull request using \`gh pr create\` with a clear title and description

IMPORTANT: Use \`gh\` CLI for creating the PR (GITHUB_TOKEN is already set in the environment). Do NOT use interactive flags.`;

    const escaped = fullPrompt.replace(/'/g, "'\\''");
    const cmd = `export PATH=~/.npm-global/bin:~/bin:$PATH && ANTHROPIC_API_KEY='${this.config.anthropicApiKey}' GITHUB_TOKEN='${this.config.githubToken}' claude -p '${escaped}' --dangerously-skip-permissions --output-format=stream-json --model ${this.config.claudeModel} --verbose`;

    console.log(`[${label}] Executing claude command via session with log streaming...`);

    const sessionId = `claude-${label}-${Date.now()}`;
    await sandbox.process.createSession(sessionId);

    const command = await sandbox.process.executeSessionCommand(sessionId, {
      command: `cd ${repoDir} && ${cmd}`,
      runAsync: true,
    });

    let stdoutBuffer = "";

    await sandbox.process.getSessionCommandLogs(
      sessionId,
      command.cmdId!,
      (chunk: string) => {
        stdoutBuffer += chunk;
        const lines = stdoutBuffer.split("\n");
        // Keep the last (potentially incomplete) line in the buffer
        stdoutBuffer = lines.pop()!;

        for (const line of lines) {
          this.handleStreamLine(line, label);
        }
      },
      (chunk: string) => {
        process.stderr.write(`[${label}] ${chunk}`);
      }
    );

    // Flush any remaining buffered content
    if (stdoutBuffer.trim()) {
      this.handleStreamLine(stdoutBuffer, label);
    }

    console.log(`[${label}] Claude command completed`);
  }

  private handleStreamLine(line: string, label: string): void {
    const trimmed = line.trim();
    if (!trimmed || !trimmed.startsWith("{")) return;

    try {
      const event = JSON.parse(trimmed);

      if (event.type === "assistant" && event.message?.content) {
        for (const block of event.message.content) {
          if (block.type === "text") {
            console.log(`[${label}] ${block.text}`);
          }
        }
      } else if (event.type === "result") {
        console.log(`[${label}] Result: ${event.result}`);
      }
    } catch {
      // Partial or malformed JSON — skip
    }
  }

