    console.log(`[${label}] Starting Claude via PTY...`);

    const decoder = new TextDecoder();
    let buffer = "";

    const pty = await sandbox.process.createPty({
      id: `claude-${label}-${Date.now()}`,
      cwd: repoDir,
      envs: {
        ANTHROPIC_API_KEY: this.config.anthropicApiKey,
